import React, { useState } from 'react';
import axios from 'axios';
import styles from './mystyle.module.css'; 



const Upload = () => {

    const [selectedFile, setSelectedFile] = useState();
    const [isFilePicked, setIsFilePicked] = useState(false);
	const [isFileSubmitted, setIsFileSubmitted] = useState(false);
	const [selectedHeaders, setSelectedHeaders] = useState();
	const d = new Date();
	

	
   
    const changeHandler = (event) => {
		setSelectedFile(event.target.files[0]);
		console.log(event.target.files[0]);
		setIsFilePicked(true);
	};
    //uploading csv to server
    const handleSubmission = () => {
		console.log("handleUpload started");
        const data = new FormData()
        data.append('file', selectedFile)
        axios.post("http://localhost:3001/upload", data, {

        })
        .then(res => {
            console.log(res.statusText);
        })
		handleDatabase();
		submit();
		// getHeadersCsv();
	};
	



	//getHeaders from selectedfile csv
	const submit = () => {
        const file = selectedFile;
        const reader = new FileReader();

        reader.onload = function(e) {
            const text = e.target.result;
            console.log(text);
			processCsv(text);
        }

        reader.readAsText(file);
    }
	
	const processCsv = (str, delim=",") => {
		setSelectedHeaders(  str.slice(0,str.indexOf('\n')).split(delim));
		console.log(selectedHeaders);
		
		// const rows = str.slice(str.indexOf('\n')+1).split('\n');
		// console.log(rows);

		// const newArray = rows.map( row=> {
		// 	const values = row.split(delim);
		// 	const eachObject = headers.reduce((obj, header, i) => {
		// 		obj[header] = values[i];
		// 		return obj;
		// 	}, {})
		// 	return eachObject;
		// })
	}



	//Date.now() + '-' + file.originalname
	//putting csvfile name in database
	const handleDatabase = ()=> {
		console.log("handleDB started");
		let csvName = d.getDate() + '-' + (d.getMonth()+1) + '-' + selectedFile.name;
		let graphName = 'histogram';
		let var1m="m";
		let var2m="p";
		let var3m="g";
		axios.post("http://localhost:3001/list",{
			csvname: csvName,
			graph: graphName,
			var1: var1m,
			var2: var2m,
			var3: var3m,
		})
		.then(res => {
            console.log(res.statusText);
        })

		setIsFileSubmitted(true);
	
		// fetch("http://localhost:3001/list", {
		// 	method: 'POST',
		// 	headers: {
		// 		'Content-Type' : 'application/json',
		// 	},
		// 	body: JSON.stringify({csvName}),
		// }).catch(error => {
		// 	console.log(error);
		// })
		// .then(response => {
		// 	console.log(response);
		// 	return response.text();
		// });
		
	};

	


    return (
        <div>
			<input type="file" name="file" accept=".csv" onChange={changeHandler} />
			{isFilePicked ? (
				<div>
					<p>Filename: {selectedFile.name}</p>
					<p>Filetype: {selectedFile.type}</p>
					<p>Size in bytes: {selectedFile.size}</p>
					<p>
						lastModifiedDate:{' '}
						{selectedFile.lastModifiedDate.toLocaleDateString()}
					</p>
				</div>
			) : (
				<p>Select a file to show details</p>
			)}
			<div>
				<button onClick={ handleSubmission } >Submit</button>
			</div>
			{isFileSubmitted ? (
				<div> 
					<label>Variables</label>
					{selectedHeaders.map((e) => (
						<button type="button">{e}</button>
					))}
				</div>
			) : ( <br/>) }
		</div>
    )
}

export default Upload;
