import React, { useState } from 'react';
import axios from 'axios';
import Modal from 'react-bootstrap/Modal';
import Button  from 'react-bootstrap/Button';
//stepper
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
	root: {
	  width: '100%',
	},
	backButton: {
	  marginRight: theme.spacing(1),
	},
	instructions: {
	  marginTop: theme.spacing(1),
	  marginBottom: theme.spacing(1),
	},
  }));



//"react-bootstrap": "^2.0.0-beta.4",
 



//logic
const Upload = () => {
	
	const [activeStep, setActiveStep] = React.useState(0);
	const steps = getSteps();
  
	const handleNext = () => {
	  setActiveStep((prevActiveStep) => prevActiveStep + 1);
	};
  
	const handleBack = () => {
	  setActiveStep((prevActiveStep) => prevActiveStep - 1);
	};
  
	const handleReset = () => {
	  setActiveStep(0);
	};
	function getSteps() {
		return ['Upload CSV', 'Choose variables', 'Choose a graphicon'];
	}
	
	function case0 (){
		<input type="file" name="file" accept=".csv" onChange={changeHandler} />
			{isFilePicked ? (
				<div>
					<p>Filename: {selectedFile.name}</p>
					<p>Filetype: {selectedFile.type}</p>
					<p>Size in bytes: {selectedFile.size}</p>
					<p>
						lastModifiedDate:{' '}
						{selectedFile.lastModifiedDate.toLocaleDateString()}
					</p>
				</div>
			) : (
				<p>Select a file to show details</p>
			)}
			<div>
				<button onClick={ handleSubmission } >Submit</button>
			</div>;
	}
	function getStepContent(stepIndex) {
		switch (stepIndex) {
		  case 0:
			return case0();
		  case 1:
			return 'What is an ad group anyways?';
		  case 2:
			return 'This is the bit I really care about!';
		  default:
			return 'Unknown stepIndex';
		}
	  }

	
function MyVerticallyCenteredModal(props){
	const classes = useStyles();
	return(
		<Modal	
			{...props}
			size="lg"
			aria-labelledby="contained-modal-title-vcenter"
			centered
		>
			<Modal.Header closeButton>
				<Modal.Title id="contained-modal-title-vcenter">
					Modal heading
				</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				
				{/* stepper */}
				<div className={classes.root}>
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>All steps completed</Typography>
            <Button onClick={handleReset}>Reset</Button>
          </div>
        ) : (
          <div>
            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
            <div>
              <Button
                disabled={activeStep === 0}
                onClick={handleBack}
                className={classes.backButton}
              >
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
				
				



			</Modal.Body>
			<Modal.Footer>
				<Button onClick={props.onHide}>Close</Button>
			</Modal.Footer>
		</Modal>

	);
}
	
	  //end of steppper
	const [ modalShow, setModalShow ] = useState(false);
    const [selectedFile, setSelectedFile] = useState();
    const [isFilePicked, setIsFilePicked] = useState(false);
	const [isFileSubmitted, setIsFileSubmitted] = useState(false);
	const [selectedHeaders, setSelectedHeaders] = useState();
	const [isHeaderPicked, setIsHeaderPicked] = useState(false);
	const d = new Date();
	let chosenVariables = [];
	let typesGraphicons = ['Linijski grafikon', 'Histogram','Box plot','Scatter plot', 'Bar plot', 'Heat map'];
	let chosenGraph = -1;
	

	
   
    const changeHandler = (event) => {
		setSelectedFile(event.target.files[0]);
		console.log(event.target.files[0]);
		setIsFilePicked(true);
	};
    //uploading csv to server
    const handleSubmission = () => {
		console.log("handleUpload started");
        const data = new FormData()
        data.append('file', selectedFile)
        axios.post("http://localhost:3001/upload", data, {

        })
        .then(res => {
            console.log(res.statusText);
        })
		
		submit();
		setIsFileSubmitted(true);
	};
	



	//getHeaders from selectedfile csv
	const submit = () => {
        const file = selectedFile;
        const reader = new FileReader();

        reader.onload = function(e) {
            const text = e.target.result;
            console.log(text);
			processCsv(text);
        }

        reader.readAsText(file);
    }
	
	const processCsv = (str, delim=",") => {
		setSelectedHeaders(  str.slice(0,str.indexOf('\r\n')).split(delim));
		setIsHeaderPicked(true);
		console.log(selectedHeaders);
		
	}



	

	const saveChosenVariables = (e) => {
		if(chosenVariables.indexOf(e) === -1){
			chosenVariables.push(e);
			console.log(chosenVariables);
		}
		else if(chosenVariables.indexOf(e) !== -1){
			chosenVariables.splice(chosenVariables.indexOf(e),1);
			console.log(chosenVariables);
		}
		
		
	}

	//Date.now() + '-' + file.originalname
	//putting csvfile name in database
	const handleDatabase = ()=> {
		console.log("handleDB started");
		let csvName = d.getDate() + '-' + (d.getMonth()+1) + '-' + selectedFile.name;
		
		axios.post("http://localhost:3001/list",{
			csvname: csvName,
			graph: chosenGraph,
			var1: chosenVariables[0],
			var2: chosenVariables[1],
			var3: chosenVariables[2],
			var4: chosenVariables[3],
			var5: chosenVariables[4],
		})
		.then(res => {
            console.log(res.statusText);
        })

		
	
		// fetch("http://localhost:3001/list", {
		// 	method: 'POST',
		// 	headers: {
		// 		'Content-Type' : 'application/json',
		// 	},
		// 	body: JSON.stringify({csvName}),
		// }).catch(error => {
		// 	console.log(error);
		// })
		// .then(response => {
		// 	console.log(response);
		// 	return response.text();
		// });

		
		
	};

    return (
		






		//logic htmnl
        <div>
			<Button variant="primary" onClick={() => setModalShow(true)}>
				Launch vertically centered modal
			</Button>

			<MyVerticallyCenteredModal
				show={modalShow}
				onHide={() => setModalShow(false)}
			/>
			
			<input type="file" name="file" accept=".csv" onChange={changeHandler} />
			{isFilePicked ? (
				<div>
					<p>Filename: {selectedFile.name}</p>
					<p>Filetype: {selectedFile.type}</p>
					<p>Size in bytes: {selectedFile.size}</p>
					<p>
						lastModifiedDate:{' '}
						{selectedFile.lastModifiedDate.toLocaleDateString()}
					</p>
				</div>
			) : (
				<p>Select a file to show details</p>
			)}
			<div>
				<button onClick={ handleSubmission } >Submit</button>
			</div>
			{isFileSubmitted ? (
				<>
				<div> 
					<label>Variables</label>
					{
							isHeaderPicked ? (
								selectedHeaders.map((e,i) => (
										<button key={i} type="button" onClick={()=> {saveChosenVariables(e) }}>{e}</button>))
							) : ( <br/>) 
					
					}
					{console.log(chosenVariables)}
					
				</div>
				<div>
				<label>Graphicons</label>
				{typesGraphicons.map((e,i) => (
					<button key={i} type="button" onClick={() => {chosenGraph = e; console.log(chosenGraph);}}>{e}</button>
				))}

				</div>
				<div>
					<button type="submit" onClick={()=>{handleDatabase()}}>Napravi grafikon</button>
				</div>
				</>
				
			) : ( <br/>) }
			
			
		</div>
		
    )
}

export default Upload;
