import React, { useState } from 'react';
import axios from 'axios';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import './mystyle.css'; 

import LineGraph from './pictures/linegraph.png';

//stepper





//"react-bootstrap": "^2.0.0-beta.4",

function MyBody(props) {
	const [selectedFile, setSelectedFile] = useState();
	const [isFilePicked, setIsFilePicked] = useState(false);
	const [isFileSubmitted, setIsFileSubmitted] = useState(false);
	const [selectedHeaders, setSelectedHeaders] = useState();
	const [isHeaderPicked, setIsHeaderPicked] = useState(false);
	const [isGraphDone, setIsGraphDone] = useState(false);
	const [isInput, setIsInput] = useState(true);
	const [LinkOfGraph, setLinkOfGraph] = useState('http://localhost:3001/graph');
	const [iFrameLink, setIframeLink] = useState(false);

	const d = new Date();
	let chosenVariables = [];
	let typesGraphicons = ['Linijski grafikon', 'Histogram', 'Box plot', 'Scatter plot', 'Bar plot', 'Heat map'];
	let chosenGraph = -1;
	let lastId = "";





	const changeHandler = (event) => {
		setSelectedFile(event.target.files[0]);
		console.log(event.target.files[0]);
		setIsFilePicked(true);
	};
	//uploading csv to server
	const handleSubmission = () => {
		console.log("handleUpload started");
		const data = new FormData()
		data.append('file', selectedFile)
		axios.post("http://localhost:3001/upload", data, {

		})
			.then(res => {
				console.log(res.statusText);
			})

		submit();
		setIsFileSubmitted(true);
	};




	//getHeaders from selectedfile csv
	const submit = () => {
		const file = selectedFile;
		const reader = new FileReader();

		reader.onload = function (e) {
			const text = e.target.result;
			console.log(text);
			processCsv(text);
		}

		reader.readAsText(file);
	}

	const processCsv = (str, delim = ",") => {
		setSelectedHeaders(str.slice(0, str.indexOf('\r\n')).split(delim));
		setIsHeaderPicked(true);
		console.log(selectedHeaders);

	}





	const saveChosenVariables = (e) => {
		if (chosenVariables.indexOf(e) === -1) {
			chosenVariables.push(e);
			console.log(chosenVariables);
		}
		else if (chosenVariables.indexOf(e) !== -1) {
			chosenVariables.splice(chosenVariables.indexOf(e), 1);
			console.log(chosenVariables);
		}


	}

	//Date.now() + '-' + file.originalname
	//putting csvfile name in database
	const handleDatabase = () => {
		console.log("handleDB started");
		let csvName = d.getDate() + '-' + (d.getMonth() + 1) + '-' + selectedFile.name;

		axios.post("http://localhost:3001/list", {

			csvname: csvName,
			graph: chosenGraph,
			var1: chosenVariables[0],
			var2: chosenVariables[1],
			var3: chosenVariables[2],
			var4: chosenVariables[3],
			var5: chosenVariables[4],
		})
			.then(res => {
				console.log(res.statusText);
				console.log(res);
				console.log("1");
				function sendId() {
					return new Promise(resolve => {
						lastId = res.data.rows[0].id;
						console.log(lastId);
						setLinkOfGraph(LinkOfGraph + lastId);
						console.log(typeof LinkOfGraph);
						console.log(LinkOfGraph);
						console.log(res.data.rows[0].id);
						console.log("2");
						resolve(LinkOfGraph);

					})
				}
				async function sent() {
					await sendId();
					setIsFileSubmitted(false);
					setIsFilePicked(false);
					console.log(isInput);
					setIsInput(false);
					console.log(isInput);

					console.log("3");
					setIsGraphDone(true);

					console.log("graph done");
				}
				sent();





			})




	}
	return (<div>
		{isInput ? (<input type="file" name="file" className="btn btn-danger" accept=".csv" onChange={changeHandler} />) : (<></>)}
		{/* 4 korak */}
		{(isFilePicked && isInput) ? (
			<>
			
			<div>
				<p>Filename: {selectedFile.name}</p>
				<p>Filetype: {selectedFile.type}</p>
				<p>Size in bytes: {selectedFile.size}</p>
				<p>
					lastModifiedDate:{' '}
					{selectedFile.lastModifiedDate.toLocaleDateString()}
				</p>
				<div>
					<Button variant="danger" onClick={handleSubmission} >Submit</Button>
				</div>
				
			</div>
			<br/>
			</>

		) : (
			<></>)
		}

		{isFileSubmitted ? (
			<>
				{/* 6 korak */}
				<div>
					<label>Variables:  </label>
					{
						isHeaderPicked ? (
							selectedHeaders.map((e, i) => (
								<Button variant="danger" className="button-spacing" key={i} onClick={() => { saveChosenVariables(e) }}>{e}</Button>))
						) : (<></>)

					}
					{console.log(chosenVariables)}
					

				</div>
				<br/>
				<div>
					<label>Graphicons:  </label>
					{/* 7 korak */}
					{typesGraphicons.map((e, i) => (
						<Button key={i} variant="danger" className="button-spacing" onClick={() => { chosenGraph = e; console.log(chosenGraph); }}>{e}</Button>
					))}

				</div>
				<br/>
				<div>
					<Button variant="danger" type="submit" onClick={() => { handleDatabase() }}>Napravi grafikon</Button>
				</div>
			</>

		) : (<></>)}

		{isGraphDone ? (
			// 8 korak
			<>

				{console.log(LinkOfGraph)}

				{console.log("4")}

				<iframe src={LinkOfGraph} title="graph" allowFullScreen={true} height="450" width="800
					 "></iframe>
				{console.log("5")}
				<div className="text-center">
				<Button variant="danger" onClick={()=>{setIframeLink(true)}}>Create iframe adress</Button></div>
				{iFrameLink ? (
					 <>	
					{/* //className="text-center" */}
						<div className="text-center">
							<br/>
						<div className="div-link">{LinkOfGraph}</div></div>
					</>
				) : (<> </>)}
			</>
		) : (<></>)}

	</div>


	);
}


//logic
const Upload = () => {


	//1 korak


	function MyVerticallyCenteredModal(props) {

		return (
			// 3 korak
			<Modal
				{...props}
				size="lg"
				aria-labelledby="contained-modal-title-vcenter"
				centered
			>
				<Modal.Header closeButton>
					<Modal.Title id="contained-modal-title-vcenter">
						d3.js grafikon
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>


					<MyBody />


					
				</Modal.Body>
				<Modal.Footer>
					
					<Button variant="danger" onClick={props.onHide}>Close</Button>
				</Modal.Footer>
			</Modal>

		);
	}

	//end of steppper
